import React, { useState } from "react";
import axios from "axios";

const ItemMasterForm = () => {
  const [form, setForm] = useState({
    ITEM_code: "",
    ITEM_name: "",
    ITEM_model_number: "",
    ITEM_type: "",
    ITEM_group: "",
    ITEM_category: "",
    ITEM_spec: "",
    ITEM_notes: ["", "", "", "", ""],
    ITEM_brand_name: "",
    ITEM_origin_country: "",
    ITEM_certificate: "",
    ITEM_dimension: "",
    ITEM_net_weight: "",
    ITEM_image: null,
    serial_number: "", // only for Warranty
  });

  const [showWarrantyPopup, setShowWarrantyPopup] = useState(false);

  // Handle Item Type Change
  const handleItemTypeChange = (e) => {
    const selectedType = e.target.value;
    setForm((prev) => ({ ...prev, ITEM_type: selectedType }));

    if (selectedType === "Warranty") {
      setShowWarrantyPopup(true);
    } else {
      setShowWarrantyPopup(false);
      setForm((prev) => ({ ...prev, serial_number: "" }));
    }
  };

  const handleImageChange = (e) => {
    setForm((prev) => ({ ...prev, ITEM_image: e.target.files[0] }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    const formData = new FormData();
    formData.append("ITEM_code", form.ITEM_code);
    formData.append("ITEM_name", form.ITEM_name);
    formData.append("ITEM_model_number", form.ITEM_model_number);
    formData.append("ITEM_type", form.ITEM_type);
    formData.append("ITEM_group", form.ITEM_group);
    formData.append("ITEM_category", form.ITEM_category);
    formData.append("ITEM_spec", form.ITEM_spec);
    formData.append("ITEM_notes", JSON.stringify(form.ITEM_notes));
    formData.append("ITEM_brand_name", form.ITEM_brand_name);
    formData.append("ITEM_origin_country", form.ITEM_origin_country);
    formData.append("ITEM_certificate", form.ITEM_certificate);
    formData.append("ITEM_dimension", form.ITEM_dimension);
    formData.append("ITEM_net_weight", form.ITEM_net_weight);
    formData.append("serial_number", form.serial_number);

    if (form.ITEM_image) {
      formData.append("image", form.ITEM_image);
    }

    axios
      .post("/api/item/", formData, {
        headers: { "Content-Type": "multipart/form-data" },
      })
      .then((res) => {
        alert("Item saved successfully");
      })
      .catch((err) => {
        console.error(err);
        alert("Error saving item");
      });
  };

  return (
    <div className="item-master-form">
      <h2>Item Master Form</h2>

      <form onSubmit={handleSubmit}>
        <input
          type="text"
          placeholder="Item Code"
          value={form.ITEM_code}
          onChange={(e) => setForm({ ...form, ITEM_code: e.target.value })}
        />

        <input
          type="text"
          placeholder="Item Name"
          value={form.ITEM_name}
          onChange={(e) => setForm({ ...form, ITEM_name: e.target.value })}
        />

        <input
          type="text"
          placeholder="Model Number"
          value={form.ITEM_model_number}
          onChange={(e) => setForm({ ...form, ITEM_model_number: e.target.value })}
        />

        <select value={form.ITEM_type} onChange={handleItemTypeChange}>
          <option value="">Select Item Type</option>
          <option value="Warranty">Warranty</option>
          <option value="Utensil">Utensil</option>
          <option value="Spare Part">Spare Part</option>
        </select>

        <input
          type="text"
          placeholder="Group"
          value={form.ITEM_group}
          onChange={(e) => setForm({ ...form, ITEM_group: e.target.value })}
        />

        <input
          type="text"
          placeholder="Category"
          value={form.ITEM_category}
          onChange={(e) => setForm({ ...form, ITEM_category: e.target.value })}
        />

        <input
          type="text"
          placeholder="Spec / Capacity"
          value={form.ITEM_spec}
          onChange={(e) => setForm({ ...form, ITEM_spec: e.target.value })}
        />

        <label>Special Notes:</label>
        {[...Array(5)].map((_, i) => (
          <input
            key={i}
            type="text"
            placeholder={`Special Note 0${i + 1}`}
            value={form.ITEM_notes[i]}
            onChange={(e) => {
              const newNotes = [...form.ITEM_notes];
              newNotes[i] = e.target.value;
              setForm((prev) => ({ ...prev, ITEM_notes: newNotes }));
            }}
          />
        ))}

        <input
          type="text"
          placeholder="Brand Name"
          value={form.ITEM_brand_name}
          onChange={(e) => setForm({ ...form, ITEM_brand_name: e.target.value })}
        />

        <input
          type="text"
          placeholder="Country of Origin"
          value={form.ITEM_origin_country}
          onChange={(e) => setForm({ ...form, ITEM_origin_country: e.target.value })}
        />

        <input
          type="text"
          placeholder="Certificate"
          value={form.ITEM_certificate}
          onChange={(e) => setForm({ ...form, ITEM_certificate: e.target.value })}
        />

        <input
          type="text"
          placeholder="Dimension"
          value={form.ITEM_dimension}
          onChange={(e) => setForm({ ...form, ITEM_dimension: e.target.value })}
        />

        <input
          type="text"
          placeholder="Net Weight"
          value={form.ITEM_net_weight}
          onChange={(e) => setForm({ ...form, ITEM_net_weight: e.target.value })}
        />

        <label>Upload Image:</label>
        <input type="file" accept="image/*" onChange={handleImageChange} />

        <button type="submit">Save Item</button>
      </form>

      {/* Warranty Popup */}
      {showWarrantyPopup && (
        <div className="popup">
          <div className="popup-content">
            <h3>Warranty Serial Number</h3>
            <input
              type="text"
              value={form.serial_number}
              onChange={(e) => setForm((prev) => ({ ...prev, serial_number: e.target.value }))}
            />
            <button onClick={() => setShowWarrantyPopup(false)}>OK</button>
          </div>
        </div>
      )}
    </div>
  );
};

export default ItemMasterForm;
