import React, { useState } from 'react';
import './QuotationOverview.css';

const QuotationOverview = () => {
  const [header, setHeader] = useState({
    code: '', payType: '', validity: '', location: '', ref: '', customer: '', attention: ''
  });
  const [items, setItems] = useState([]);
  const [item, setItem] = useState({ description: '', code: '', qty: '', rate: '' });
  const [grossValue, setGrossValue] = useState(0);
  const [netValue, setNetValue] = useState(0);

  const handleHeaderChange = e => {
    const { name, value } = e.target;
    setHeader({ ...header, [name]: value });
  };
  const handleItemChange = e => {
    const { name, value } = e.target;
    setItem({ ...item, [name]: value });
  };
  const addItem = () => {
    if (!item.code || !item.qty || !item.rate) return;
    const value = parseFloat(item.qty) * parseFloat(item.rate);
    const newItems = [...items, { ...item, value }];
    setItems(newItems);
    setItem({ description: '', code: '', qty: '', rate: '' });
    updateValues(newItems);
  };
  const updateValues = (itemsList) => {
    const gross = itemsList.reduce((sum, i) => sum + i.value, 0);
    setGrossValue(gross);
    setNetValue(gross);
  };
  const saveQuotation = () => {
    alert("Save clicked");
    // Add save logic here
  };
  const deleteQuotation = () => {
    alert("Delete clicked");
    // Add delete logic here
  };
  const printQuotation = () => {
    alert("Print clicked");
    // Add print logic here
  };
  const newQuotation = () => {
    setHeader({ code: '', payType: '', validity: '', location: '', ref: '', customer: '', attention: '' });
    setItems([]);
    setGrossValue(0);
    setNetValue(0);
  };
  return (
    <div className="quotation-container">
      <div className="header-section">
        <div className="input-group">
          <label>Code</label><input name="code" value={header.code} onChange={handleHeaderChange} />
        </div>
        <div className="input-group">
          <label>Pay Type</label><input name="payType" value={header.payType} onChange={handleHeaderChange} />
        </div>
        <div className="input-group">
          <label>Validity</label><input name="validity" value={header.validity} onChange={handleHeaderChange} /> <span>Days</span>
        </div>
        <div className="input-group">
          <label>Location</label><input name="location" value={header.location} onChange={handleHeaderChange} />
        </div>
        <div className="input-group">
          <label>Your Ref</label><input name="ref" value={header.ref} onChange={handleHeaderChange} />
        </div>
        <div className="input-group">
          <label>Customer</label><input name="customer" value={header.customer} onChange={handleHeaderChange} />
        </div>
        <div className="input-group">
          <label>Attention</label><input name="attention" value={header.attention} onChange={handleHeaderChange} />
        </div>
      </div>

      <div className="item-entry">
        <div className="input-group">
          <label>Item Description</label><input name="description" value={item.description} onChange={handleItemChange} />
        </div>
        <div className="input-group">
          <label>Item</label><input name="code" value={item.code} onChange={handleItemChange} />
        </div>
        <div className="input-group">
          <label>QTY</label><input name="qty" value={item.qty} onChange={handleItemChange} />
        </div>
        <div className="input-group">
          <label>Rate</label><input name="rate" value={item.rate} onChange={handleItemChange} />
        </div>
        <button className="add-button" onClick={addItem}>Add</button>
      </div>

      <div className="items-table">
        <table>
          <thead>
            <tr>
              <th>Code</th><th>Description</th><th>QTY</th><th>Rate</th><th>Value</th>
            </tr>
          </thead>
          <tbody>
            {items.length > 0 ? (
              items.map((it, idx) => (
                <tr key={idx}>
                  <td>{it.code}</td><td>{it.description}</td><td>{it.qty}</td><td>{it.rate}</td><td>{it.value}</td>
                </tr>
              ))
            ) : (
              <tr><td colSpan="5">No Items Added</td></tr>
            )}
          </tbody>
        </table>
      </div>

      <div className="totals">
        <div>Gross Value: Rs. {grossValue.toFixed(2)}</div>
        <div>Net Value: Rs. {netValue.toFixed(2)}</div>
      </div>

      <div className="actions">
        <button onClick={saveQuotation}>Save</button>
        <button onClick={newQuotation}>New</button>
        <button onClick={deleteQuotation}>Delete</button>
        <button onClick={printQuotation}>Print</button>
      </div>
    </div>
  );
};

export default QuotationOverview;
